import React, {useState, useEffect} from 'react';
//import logo from './logo.svg';
import './App.css';
import socketIOClient from 'socket.io-client'
import RandButton from './Button'
import { Button, ButtonGroup } from '@material-ui/core';
const ENDPOINT = 'http://localhost:4001'


function App() {
    const [flower, setFlower] = useState({})
    const [responce, setResponce] = useState('')
    const [showRolls, setShowRolls] = useState(false)

    function getFlower(){
        fetch('/flower')
            .then(response => response.json())
            .then(data => {
                setFlower(data)
        });
    }
    getFlower();

    function Multiplebuttons() {
        if (showRolls === true) {
            setShowRolls(false)
        }
        else {
            setShowRolls(true)
        }
    }

    useEffect(() => {
        const socket = socketIOClient(ENDPOINT)
        socket.on("FromAPI", data => {
            setResponce(data)
        })
        return () => socket.disconnect();
    }, [])

    return (
        <div className="App">
            <h1>{flower.name}</h1>
            <p>{flower.color}</p>
            <h1><RandButton Number={4} Bonus={1} /></h1>
            <h1><RandButton Number={6} Bonus={0} /></h1>
            <h1><RandButton Number={10} Bonus={0} /></h1>
            <h1><RandButton Number={20} Bonus={0} /></h1>
            {showRolls
                ? <ButtonGroup color="primary" aria-label="outlined primary button group">
                    <h1><RandButton Number={4} Bonus={1} /></h1>
                    <h1><RandButton Number={6} Bonus={0} /></h1>
                    <h1><RandButton Number={10} Bonus={0} /></h1>
                </ButtonGroup>
                : null
            }
            <Button variant="outlined" onClick={() => Multiplebuttons()}> Roll </Button>
            <p>It's <time dateTime = {responce}>{responce}</time></p>
        </div>
    );
}

export default App;
